import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ShoppingService {

  products = new BehaviorSubject([
    { 
      id: 1,
      label: "Cucumber",
      img: "data:image/jpeg;base64,",
      quantity: 0,
      price: 2.5,
      description: "English cucumber is a variety of seedless cucumber that is longer and slimmer than other varieties and have a higher water content. They do not have a layer of wax on them, and the skin is tender when ripe.",
      inMyCart: false,
      maxquantity :30
    },
    {
      id: 2,
      label: "Apple",
      img: "data:image/jpeg;base64,",
      quantity: 0,
      price: 3,
      description: "The bright red coloured and heart shaped Red Delicious apples are crunchy, juicy and slightly sweet. Red Delicious Apples are a natural source of fibre and are fat free. They contain a wide variety of anti-oxidants and polynutrients. ",
      inMyCart: false,
      maxquantity :30
    },
    { id: 3,
      label: "Ginger",
      img: "data:image/jpeg;base64,",
      quantity: 0,
      price: 5,
      description: "Fresh Organic products are organically grown and handpicked by expert. ",
      inMyCart: false,
      maxquantity :30
    },
  ])
  constructor() { }

  addQuantity(id){
    let currentProducts = this.products.getValue()
    const product = this.findProduct( currentProducts,id)
    product.quantity++
    this.products.next(currentProducts)
  }

  minusQuantity(id){
    let currentProducts = this.products.getValue()
    const product = this.findProduct( currentProducts,id)
    product.quantity--
    this.products.next(currentProducts)
  }

  addToCart(id){
    let currentProducts = this.products.getValue()
    const product = this.findProduct( currentProducts,id)
    product.inMyCart = true
    this.products.next(currentProducts)
  }

  removeFromCart(id){
    let currentProducts = this.products.getValue()
    const product = this.findProduct( currentProducts,id)
    product.inMyCart = false
    product.quantity = 0
    this.products.next(currentProducts)
  }

  findProduct(currentProducts, id){
    for(let product of currentProducts){
      if(product.id === id){
        return product
      }
    }
    return null
  }


}
